import GetAvatar from "../GetAvatar/GetAvatar";

const Form = ({
  data,
  errors,
  handleInput,
  url,
  successMessage,
  handleClickCreateCard,
  errorMessage,
  handleChangeForm,
}) => {
  const handleImage = (fileImage) => {
    handleChangeForm("image", fileImage);
  };

  const handlePhoto = (filePhoto) => {
    handleChangeForm("photo", filePhoto);
  };

  return (
    <section className="form">
      <h2 className="title">Información</h2>

      <section className="ask-info">
        <p className="subtitle">Cuéntanos sobre el proyecto</p>
        <hr className="line" />
      </section>

      <fieldset className="project">
        <input
          className={`input ${errors.name ? "error" : ""}`}
          type="text"
          placeholder="Nombre del proyecto"
          name="name"
          id="name"
          value={data.name} //props.algo?
          onInput={handleInput}
        />
        {errors.name && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}
        <input
          className={`input ${errors.slogan ? "error" : ""}`}
          type="text"
          name="slogan"
          id="slogan"
          placeholder="Slogan"
          value={data.slogan}
          onInput={handleInput}
        />
        {errors.slogan && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}
        <input
          className={`input ${errors.repo ? "error" : ""}`}
          type="text"
          name="repo"
          id="repo"
          placeholder="Repo"
          value={data.repo}
          onInput={handleInput}
        />
        {errors.repo && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}
        <input
          className={`input ${errors.demo ? "error" : ""}`}
          type="text"
          placeholder="Demo"
          name="demo"
          value={data.demo}
          id="demo"
          onInput={handleInput}
        />
        {errors.demo && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}
        <input
          className={`input ${errors.technologies ? "error" : ""}`}
          type="text"
          placeholder="Tecnologías"
          name="technologies"
          value={data.technologies}
          id="technologies"
          onInput={handleInput}
        />
        {errors.technologies && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}
        <textarea
          className={`input ${errors.desc ? "error" : ""} text-desc`}
          type="text"
          placeholder="Descripción"
          name="desc"
          value={data.desc}
          id="desc"
          onInput={handleInput}
        ></textarea>
        {errors.desc && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}

      </fieldset>

      <section className="ask-info">
        <p className="subtitle">Cuéntanos sobre la autora</p>
        <hr className="line" />
      </section>

      <fieldset className="autor">
        <input
          className={`input ${errors.autor ? "error" : ""}`}
          type="text"
          placeholder="Nombre"
          name="autor"
          value={data.autor}
          id="autor"
          onInput={handleInput}
        />
        {errors.autor && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}
        <input
          className={`input ${errors.job ? "error" : ""}`}
          type="text"
          placeholder="Trabajo"
          name="job"
          value={data.job}
          id="job"
          onInput={handleInput}
        />
        {errors.job && (
          <p className="error-message">* Este campo es obligatorio</p>
        )}
      </fieldset>

      <section className="buttons-img">
        <GetAvatar
          text="Subir foto de proyecto"
          avatar={data.image}
          updateAvatar={handleImage}
        />
        <GetAvatar
          text="Subir foto de autora"
          avatar={data.photo}
          updateAvatar={handlePhoto}
        />
      </section>
      <section className="buttons-img">
        <button className="btn-large" onClick={handleClickCreateCard}>
          Crear Tarjeta
        </button>
      </section>

      <section className="card">
        {successMessage && (
          <>
            <span className="successMsg">¡Tu tarjeta ha sido creada! </span>
            <a
              href={url}
              className="successMsg-link"
              target="_blank"
              rel="noreferrer"
            >
              Click para verla
              {/* {url} */}
            </a>
          </>
        )}
        {errorMessage && (
          <>
            <p className="errorMsg">
              Error al crear la tarjeta. Por favor, revisa todos los campos.
            </p>
            <a href={url} className="errorMsg" target="_blank" rel="noreferrer">
              {url}
            </a>
          </>
        )}
      </section>
    </section>
  );
};
export default Form;
